import React from 'react';
import './App.css';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  clickHandler = (e) => {
    let buttons = e.target.parentElement.childNodes
    buttons.map(button => button.className(""))
     e.target.classList.add("selected")
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
          <div className="categories">
            {CATEGORIES.map(cat => <button onClick={(e) => this.clickHandler(e)}>{cat}</button>)}
          </div>

      </div>
    );
  }
}


export default App;
